        ArrayList<Integer> output = new ArrayList<>();
        for(int i = 0; i < arr.size(); i++) {
            count[arr.get(i)]++;
        }

        for(int i = 0; i < arr.size(); i++) {
            output.add(count[arr.get(i)]);
        }

